import { useState } from "react";
import ProfileFactory from "./components/ProfileFactory/ProfileFactory";
import "./App.css";
import RecordList from "./components/InformationRecord/RecordList";

function App() {
  const [records, setRecords] = useState([]);

  const recordsRendering = <h2>There is no records</h2>;
  if (records.length > 0) {
    recordsRendering = <RecordList item={records} />;
  }

  const addRecordHandler = (record) => {
    setRecords(() => {
      return [record, ...records];
    });
  };

  return (
    <div>
      <ProfileFactory onAddRecord={addRecordHandler} />
      {recordsRendering}
      {/* <ErrorMessage message="Please enter bla blo!" /> */}
    </div>
  );
}

export default App;
