import { useState } from "react";
import ProfileFactory from "./components/InputField/ProfileFactory";
import "./App.css";
import ErrorMessage from "./components/ui/ErrorMessage/ErrorMessage";

function App() {
  const [records, setRecords] = useState([]);

  const recordsRendering = <h2>There is no records</h2>;
  const addRecordHandler = (record) => {
    setRecords(() => {
      return [record, ...records];
    });
  };

  return (
    <div>
      <ProfileFactory onAddRecord={addRecordHandler} />
      {recordsRendering}
      <ErrorMessage message="Please enter bla blo!" />
    </div>
  );
}

export default App;
