import { createStore } from "redux";

const initial = {
  counter: 0,
  show: true,
};

const counterReducer = (state = initial, action) => {
  if (action.type === "increase") {
    return {
      ...state,
      counter: state.counter + 1,
    };
  }

  if (action.type === "decrease") {
    return {
      counter: state.counter - 1,
    };
  }

  return state;
};

const store = createStore(counterReducer);

export default store;
